let fruits = [
    {id: 1, title: 'Яблуки', price: 20, img: 'data:image/jpeg;base64,'},
    {id: 2, title: 'Апельсини', price: 30, img: 'data:image/jpeg;base64,'},
    {id: 3, title: 'Манго', price: 40, img: 'data:image/jpeg;base64,'}
]

const toHTML = fruit => `
<div class="col">
    <div class="card" style="height: 370px; width: 320px">
        <img src="${fruit.img}" class="card-img-top" alt="${fruit.title}" style="height: 250px; width: 250px">
        <div class="card-body">
        <h5 class="card-title">${fruit.title}</h5>
        <a href="#" class="btn btn-primary" data-btn='price' data-id="${fruit.id}">Подивитись ціну</a>
        <a href="#" class="btn btn-danger" data-btn='remove' data-id="${fruit.id}">Видалити</a>
        </div>
    </div>
</div>
`
function render() {
    const html = fruits.map(toHTML).join('')
    document.querySelector('#fruits').innerHTML = html
}
render()

const priceModal = $.modal({
    title: 'Ціна на товар',
    closable: true,
    width: '400px',
    footerButtons: [
        {text: 'Закрити', type: 'primary', handler() {
            priceModal.close()
        }}
    ]
})

document.addEventListener('click', event => {
    event.preventDefault()
    const btnType = event.target.dataset.btn
    const id = +event.target.dataset.id
    const fruit = fruits.find(f => f.id === id)

    if(btnType === 'price') {
        priceModal.setContent(`
            <p> Ціна на ${fruit.title}: <strong>${fruit.price}грн.</strong></p>
        `)
        priceModal.open()
    }else if(btnType === 'remove') {
        $.confirm({
            title: 'Ви впевнені?',
            content: `<p> Ви вадаляєте фрукт <strong>${fruit.title}</strong></p>`
        }).then(() => {
            fruits = fruits.filter(f => f.id !== id)
            render()
        }).catch(() => {
            console.log('cancel')
        })
    }
})